INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336438, 'Ethanol', 'Pitavastatin', 'Moderate', 'Concomitant use of statin medication with substantial quantities of alcohol may increase the risk of hepatic injury. Transient increases in serum transaminases have been reported with statin use and while these increases generally resolve or improve with continued therapy or a brief interruption in therapy, there have been rare postmarketing reports of fatal and non-fatal hepatic failure in patients taking statins. Patients who consume substantial quantities of alcohol and/or have a history of liver disease may be at increased risk for hepatic injury. Active liver disease or unexplained transaminase elevations are contraindications to statin use.', 'DDInter', 1767317475, 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', 'Synergism', 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
