INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336454, 'Ethanol', 'Propofol', 'Moderate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents. Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 1767317475, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', 'Synergism', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336455, 'Ethanol', 'Dextropropoxyphene', 'Major', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS- and/or respiratory-depressant effects with propoxyphene. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'DDInter', 1767317475, 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be appropriate. Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression and other CNS adverse effects. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336456, 'Ethanol', 'Propranolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336457, 'Ethanol', 'Protriptyline', 'Moderate', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills. Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'DDInter', 1767317475, 'Patients should be advised to avoid alcohol during TCA therapy. Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy. Dosage adjustments may be required.', 'Metabolism', 'Patients should be advised to avoid alcohol during TCA therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
