INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336459, 'Ethanol', 'Quazepam', 'Moderate', 'Acute ethanol ingestion may potentiate the CNS effects of many benzodiazepines. Tolerance may develop with chronic ethanol use. The mechanism may be decreased clearance of the benzodiazepines because of CYP450 hepatic enzyme inhibition. Also, it has been suggested that the cognitive deficits induced by benzodiazepines may be increased in patients who chronically consume large amounts of alcohol.', 'DDInter', 1767317475, 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', 'Metabolism', 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
