INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336495, 'Ethanol', 'Tadalafil', 'Moderate', 'Additive hypotensive effects may occur when phosphodiesterase-5 (PDE5) inhibitors are used with alcohol, as both are mild systemic vasodilators.', 'DDInter', 1767317475, 'Patients taking PDE5 inhibitors should avoid consuming large amounts of alcohol, which may increase the potential for orthostatic signs and symptoms including increase in heart rate, decrease in standing blood pressure, dizziness, and headache.', 'Synergism', 'Patients taking PDE5 inhibitors should avoid consuming large amounts of alcohol, which may increase the potential for orthostatic signs and symptoms including increase in heart rate, decrease in standing blood pressure, dizziness, and headache.', NULL, NULL, 'Moderate', 'pharmacodynamic');
