INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336557, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Aldesleukin', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 1767317475, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336558, 'Alefacept', 'Aldesleukin', 'Moderate', 'The use of alefacept with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Patients receiving other immunosuppressive or myelosuppressive agents or phototherapy should not be treated with alefacept because of the possibility of excessive immunosuppression.', 'Others', 'Patients receiving other immunosuppressive or myelosuppressive agents or phototherapy should not be treated with alefacept because of the possibility of excessive immunosuppression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336559, 'Alemtuzumab', 'Aldesleukin', 'Moderate', 'The use of alemtuzumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Alemtuzumab alone may cause severe and prolonged myelosuppression, lymphopenia, and rarely, fatal bone marrow aplasia/hypoplasia, autoimmune idiopathic thrombocytopenia, and autoimmune hemolytic anemia. Serious, sometimes fatal opportunistic infections have been reported, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. The manufacturer recommends that single doses of alemtuzumab not exceed 30 mg and cumulative weekly doses not exceed 90 mg, since higher dosages are associated with an increased incidence of pancytopenia. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Synergism', 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336560, 'Alfentanil', 'Aldesleukin', 'Moderate', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS-depressant effects with aldesleukin. Aldesleukin has been associated with CNS effects including depression, confusion, and drowsiness, which may progress to coma.', 'DDInter', 1767317475, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications. Patients should be monitored for CNS depression and other CNS adverse effects. Treatment with aldesleukin should be discontinued if severe lethargy or somnolence develop.', 'Synergism', 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
