INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336584, 'Aldesleukin', 'Betamethasone', 'Moderate', 'Glucocorticoids may antagonize the immunoregulatory properties of aldesleukin and reduce its antitumor effectiveness.', 'DDInter', 1767317475, 'Although glucocorticoids can attenuate aldesleukin-induced side effects including fever, renal insufficiency, hyperbilirubinemia, confusion and dyspnea, concomitant administration of these agents with aldesleukin should generally be avoided. However, patients who develop life-threatening signs or symptoms may be treated with dexamethasone until toxicity resolves to an acceptable level.', 'Antagonism', 'Although glucocorticoids can attenuate aldesleukin-induced side effects including fever, renal insufficiency, hyperbilirubinemia, confusion and dyspnea, concomitant administration of these agents with aldesleukin should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
