INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21130, 'Amiloride', 'Tolvaptan', 'Moderate', 'Coadministration with tolvaptan can potentiate the risk of hyperkalemia in patients treated with other drugs that are known to increase serum potassium. Tolvaptan alone can increase serum potassium by causing an acute reduction of the extracellular fluid volume.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with tolvaptan and other agents that can increase serum potassium such as ACE inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, and miscellaneous agents like cyclosporin, heparin, tacrolimus, and trimethoprim.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with tolvaptan and other agents that can increase serum potassium such as ACE inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, and miscellaneous agents like cyclosporin, heparin, tacrolimus, and trimethoprim. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21145/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Samsca (tolvaptan)." Otsuka A