INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21397, 'Amiodarone', 'Clarithromycin', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Coadministration with other agents that can prolong the QT interval such as macrolide antibiotics may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.In general, the risk of an individual agent or a combination of agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia). Some macrolides such as clarithromycin, erythromycin, and troleandomycin are also potent inhibitors of CYP450 3A4 and may significantly inhibit the metabolism of amiodarone, which is primarily metabolized by the isoenzyme.', NULL, 'The concurrent use of amiodarone with other medications that can prolong the QT interval should generally be avoided unless benefits are anticipated to outweigh the risks.', NULL, 'The concurrent use of amiodarone with other medications that can prolong the QT interval should generally be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if these agents are prescribed together, especially to patients with underlying risk factors. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21412/', '[1] "Product Information. Azithromycin Product Information (azithromycin)." Pfizer US Pharmaceuticals, New York, NY.[2] Trivier JM, Libersa C, Belloc C, Lhermitte M "Amiodarone N-deethylation in human liver microsomes: involvement of cytochrome P450 3A enzymes (first report)." Life Sci 52 (1993): pl91-6[3] Samarendra P, Kumari S, Evans SJ, Sacchi TJ, Navarro V "QT prolongation associated with azithromycin/amiodarone combination." Pacing Clin Electrophysiol 24 (2001): 1572-4[4] Yamreudeewong W, DeBisschop M, Martin L, Lower D "Potentially Significant Drug Interactions of Class III Antiarrhythmic Drugs." Drug Saf 26 (2003): 421-38[5] "Product Information. Erythrocin Lactobionate (erythromycin)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Nattel S, Ranger S, Talajic M, et al "Erythromycin-induced long QT syndrome: concordance with quinidine and underlying cellular electrophysiologic mechanism." Am J Med 89 (1990): 235-8[8] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[9] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[10] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21398, 'Amiodarone', 'Clobazam', 'Moderate', 'Coadministration of clobazam with inhibitors of CYP450 2C19 may significantly increase the plasma concentrations of its active metabolite, N-desmethylclobazam, which is primarily metabolized by the isoenzyme. At therapeutic dosages of clobazam, plasma concentrations of N-desmethylclobazam are 3 to 5 times higher than those of the parent compound.', NULL, 'Dosage adjustment of clobazam may be necessary when coadministered with strong (e.', NULL, 'Dosage adjustment of clobazam may be necessary when coadministered with strong (e.g., fluconazole, fluvoxamine, ticlopidine) or moderate (e.g., omeprazole) inhibitors of CYP450 2C19.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21413/', '[1] "Product Information. Onfi (clobazam)." Lundbeck Inc, Deerfield, IL.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21399, 'Amiodarone', 'Clofarabine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21414/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767363020);
