INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21410, 'Amiodarone', 'Colesevelam', 'Moderate', 'INTERVAL: Colesevelam may decrease the gastrointestinal absorption of coadministered drugs. Approximately 20% to 40% reductions in systemic exposure (AUC) and 10% to 45% reductions in peak plasma concentration (Cmax) have been reported for cyclosporine, ethinyl estradiol, glimepiride, glyburide, levothyroxine, olmesartan, and sustained-release verapamil during coadministration with colesevelam.', NULL, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose.', NULL, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose. Clinical and laboratory monitoring of drug levels and effects should be considered.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21425/', '[1] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[6] "Position Statement: evidence-based