INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21431, 'Amiodarone', 'Diatrizoate', 'Moderate', 'The use of iodine-containing contrast media for coronary angiography in patients treated with certain antiarrhythmics such as amiodarone may result in significant prolongation of the QT interval. These contrast agents can be arrhythmogenic when injected into the coronary arteries and may have additive effects on cardiac repolarization when coadministered with antiarrhythmic agents that prolong the QT interval.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, ibutilide, sotalol) antiarrhythmic agents. Increased surveillance and ECG monitoring may be appropriate. Patients who receive outpatient angiographies should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of arrhythmia such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21446/', '[1] Goernig M, Kirmeier T, Krack A, Hartog CS, Figulla HR, Leder U "Iohexol contrast medium induces QT prolongation in amiodarone patients." Br J Clin Pharmacol 58 (2004): 96-98[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an ant