INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21458, 'Amiodarone', 'Duloxetine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of duloxetine, which is a substrate of the isoenzyme. Theoretically, high plasma levels of duloxetine may increase the risk of serious adverse effects such as hypertension, hypertensive crisis, increased heart rate, orthostatic hypotension, syncope, and serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors.', NULL, 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors. Pharmacologic response to duloxetine should be monitored more closely whenever a CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21473/', '[1] Brosen K, Skjelbo E, Rasmussen BB, Poulsen HE, Loft S "Fluvoxamine is a potent inhibitor of cytochrome P4501A2." Biochem Pharmacol 45 (1993): 1211-4[2] Martinez C, Albet C, Agundez JA, et al. "Comparative in vitro and in vivo inhibition of cytochrome P450 CYP1A2, CYP2D6, and CYP3A by H2-receptor antagonists." Clin Pharmacol Ther 65 (1999): 369-76[3] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[4] Uwe F, Strobl G, Manaut F, et al "Quinolone antibacterial agents: relationship between structure and in vitro inhibition of the human cytochrome P450 isofaorm CYP1A2." Mol Pharmacol 43 (1993): 191-9[5] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21459, 'Amiodarone', 'Dutasteride', 'Moderate', 'Based on in vitro data, coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dutasteride, which is metabolized by the isoenzyme.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21474/', '[1] "Product Information. Duagen (dutasteride)." GlaxoSmithKline Healthcare, Pittsburgh, PA.', 1767363020);
