INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21501, 'Amiodarone', 'Fluconazole', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of amiodarone. The proposed mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of amiodarone. The use of amiodarone has been associated with prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. Within the azole class, ketoconazole and itraconazole are considered potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible. Otherwise, caution and close clinical monitoring are recommended, and patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21516/', '[1] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Cordarone Intravenous (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Fabre G, Julian B, Saint-Aubert B, Joyeux H, Berger Y "Evidence for CYP3A-mediated N-deethylation of amiodarone in human liver microsomal fractions." Drug Metab Dispos 21 (1993): 978-85[4] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[5] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[6] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21502, 'Amiodarone', 'Fludarabine', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21517/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21503, 'Amiodarone', 'Fludrocortisone', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21518/', '[1] Antonelli D, Atar S, Freedberg NA, Rosenfeld T "Torsade de pointes in patients on chronic amiodarone treatment: contributing factors and drug interactions." Isr Med Assoc J 7 (2005): 163-5[2] "Product Information. Amiodarone Hydrochloride (amiodarone)." Bedford Laboratories, Bedford, OH.[3] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[4] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[5] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21504, 'Amiodarone', 'Fluoxetine', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21519/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Yamreudeewong W, DeBisschop M, Martin L, Lower D "Potentially Significant Drug Interactions of Class III Antiarrhythmic Drugs." Drug Saf 26 (2003): 421-38[3] "Product Information. Norpace (disopyramide)." Searle, Sk