INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21635, 'Amiodarone', 'Metronidazole', 'Moderate', 'QT interval prolongation and torsade de pointes have been reported with the coadministration of metronidazole and amiodarone. An interaction was suspected based on the strong temporal relation between administration of the two drugs and development of the arrhythmia. The authors suggest inhibition of CYP450 3A4 metabolism by metronidazole as a possible mechanism of interaction. However, some studies have found no effect of metronidazole on CYP450 3A4 activity.', NULL, 'Until more information is available, it may be appropriate to monitor QT interval on the ECG if amiodarone is used in combination with metronidazole.', NULL, 'Until more information is available, it may be appropriate to monitor QT interval on the ECG if amiodarone is used in combination with metronidazole. Patients treated on an outpatient basis should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21650/', '[1] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Trivier JM, Libersa C, Belloc C, Lhermitte M "Amiodarone N-deethylation in human liver microsomes: involvement of cytochrome P450 3A enzymes (first report)." Life Sci 52 (1993): pl91-6[3] Kounas SP, Letsas KP, Sideris A, Efraimidis M, Kardaras F "QT interval prolongation and torsades de pointes due to a coadministration of metronidazole and amiodarone." Pacing Clin Electrophysiol 28 (2005): 472-3[4] Haas