INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21717, 'Amiodarone', 'Pirfenidone', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 1A2 may significantly increase the plasma concentrations of pirfenidone, which is primarily (70% to 80%) metabolized by the isoenzyme. Other CYP450 isoenzymes also contribute to a minor extent, including CYP450 2C9, 2C19, 2D6, and 2E1. Concomitant inhibition of one or more of these isoenzymes in addition to CYP450 1A2 is expected to further increase the magnitude of interaction.', NULL, 'Concomitant use of pirfenidone with a drug or combination of drugs that may produce moderate or potent inhibition of both CYP450 1A2 and one or more other CYP450 isoenzymes involved in the metabolism of pirfenidone (i.', NULL, 'Concomitant use of pirfenidone with a drug or combination of drugs that may produce moderate or potent inhibition of both CYP450 1A2 and one or more other CYP450 isoenzymes involved in the metabolism of pirfenidone (i.e., CYP450 2C9, 2C19, 2D6, and 2E1) should generally be avoided. These drugs should be discontinued prior to the initiation of pirfenidone and avoided during therapy. If concomitant use cannot be avoided, patients should be closely monitored for adverse reactions such as hepatotoxicity, photosensitivity, rash, nausea, diarrhea, vomiting and dyspepsia, and consideration be given to brief interruption or permanent discontinuation of pirfenidone if clinically necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21732/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[4] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363020);
