INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21815, 'Amiodarone', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21830/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21816, 'Amiodarone', 'Talazoparib', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of the efflux transporter.', NULL, 'Concomitant use of talazoparib with amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil should preferably be avoided.', NULL, 'Concomitant use of talazoparib with amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil should preferably be avoided. If coadministration is required, talazoparib should be initiated at a reduced dosage of 0.75 mg once daily. Patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. After 3 to 5 half-lives following discontinuation of the P-gp inhibitor, the talazoparib dosage may be increased to that used prior to initiation of the P-gp inhibitor.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21831/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] "Product Information. Sporanox oral solution (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Van Peer A, Woestenborghs R, Heykants J, et al "The effects of food and dose on the oral systemic availability of itraconazole in healthy subjects." Eur