INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21888, 'Amiodarone', 'Zalcitabine', 'Moderate', 'Zalcitabine can cause peripheral neuropathy in up to one-third of patients with advanced HIV disease, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Zalcitabine-related peripheral neuropathy is a sensorimotor neuropathy characterized initially by numbness and burning dysesthesia involving the distal extremities. These symptoms may be followed by sharp shooting pains or severe continuous burning pain if the drug is not withdrawn, and progress to severe pain requiring narcotic analgesics. The neuropathy is potentially irreversible. However, with prompt discontinuation of zalcitabine, it is usually slowly reversible, although symptoms may initially progress following discontinuation.', NULL, 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible.', NULL, 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible. Otherwise, careful monitoring is recommended for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness in the extremities, particularly in patients with a low CD4 cell count or diabetes. Since the development of peripheral neuropathy appears to be dose-related, the recommended dosage of zalcitabine should not be exceeded. Patients should be advised to promptly discontinue zalcitabine therapy and contact their physician if neuropathy develops. Therapy may be reinstituted following resolution of neuropathy symptoms, but dosage should be reduced to one-half the initially recommended dosage. Zalcitabine should be permanently discontinued in patients who develop severe peripheral neuropathy during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21903/', '[1] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21889, 'Amiodarone', 'Zanubrutinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as rash, diarrhea, constipation, cough, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the zanubrutinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21904/', '[1] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767363020);
