INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22250, 'Amitriptyline', 'Brigatinib', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', NULL, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22265/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22251, 'Amitriptyline', 'Brimonidine (ophthalmic)', 'Moderate', 'Tricyclic and tetracyclic antidepressants can diminish the effects of centrally acting alpha agonists. The clinical significance, if any, of this interaction with respect to topically administered alpha-2 adrenergic receptor agonists such as brimonidine is unknown. The possibility of an additive or potentiating central nervous system (CNS)-depressant effect should be considered. Additive hypotensive effects and orthostasis may also occur, particularly during initial dosing of these agents.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with tricyclic and tetracyclic antidepressants should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, confusion, hypotension and syncope, and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with tricyclic and tetracyclic antidepressants should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, confusion, hypotension and syncope, and counseled to avoid activities requiring mental alertness until they know how these agents affect them. Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia. No data are available on the level of circulating catecholamines after withdrawal of a topical alpha-2 adrenergic receptor agonist. Since tricyclic antidepressants can affect the metabolism and uptake of circulating amines, caution is advised.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22266/', '[1] "Product Information. Mirvaso (brimonidine topical)." Galderma Laboratories Inc, Cranbury, NJ.[2] "Product Information. Alphagan (brimonidine ophthalmic)." Allergan Inc, Irvine, CA.', 1767365203);
