INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22332, 'Amitriptyline', 'Dexmethylphenidate', 'Moderate', 'The coadministration with methylphenidate may increase the serum concentrations of tricyclic antidepressants (TCAs). Case reports involving primarily methylphenidate and imipramine have suggested favorable as well as unfavorable clinical effects from this combination. In vitro studies suggest that methylphenidate may inhibit the metabolism of imipramine and other TCAs, although the extent is probably subject to considerable interindividual variation.', NULL, 'Pharmacologic response to TCAs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', NULL, 'Pharmacologic response to TCAs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22347/', '[1] Gwirtsman HE, Szuba MP, Toren L, Feist M "The antidepressant response to tricyclics in major depressives is accelerated with adjunctive use of methylphenidate." Psychopharmacol Bull 30 (1994): 157-64[2] Meyers B "Treatment of imipramine-resistant depression and lithium-refractory mania through drug interactions." Am J Psychiatry 135 (1978): 1420-1[3] Wharton RN, Perel JM, Dayton PG, Malitz S "A potential clinical use for methylphenidate with tricyclic antidepressants." Am J Psychiatry 127 (1971): 1619-25[4] Cooper TB, Simpson GM "Concomitant imipramine and methylphenidate administration: a case report." Am J Psychiatry 130 (1973): 721[5] Burke MS, Josephson A, Lightsey A "Combined methylphenidate and imipramine complication." J Am Acad Child Adolesc Psychiatry 34 (1995): 403-4[6] Grob CS, Coyle JT "Suspected adverse methylphenidate-imipramine interactions in children." J Dev Behav Pediatr 7 (1986): 265-7[7] Zeidenberg P, Perel JM, Kanzler M, Wharton RN, Malitz S "Clinical and metabolic studies with imipramine in man." Am J Psychiatry 127 (1971): 1321-6', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22333, 'Amitriptyline', 'Dextroamphetamine', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', NULL, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', NULL, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22348/', '[1] Haritos VS, Ghabrial H, Ahokas JT, Ching MS "Role of cytochrome P450 2D6 (CYP2D6) in the stereospecific metabolism of E- and Z-doxepin." Pharmacogenetics 10 (2000): 591-603[2] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[3] Raisfeld IH "Cardiovascular complications of antidepressant therapy: interactions at the adrenergic neuron." Am Heart J 83 (1972): 129-33[4] Markowit