INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22497, 'Amitriptyline', 'Lisinopril', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22512/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22498, 'Amitriptyline', 'Lithium carbonate', 'Moderate', 'The concomitant use of lithium and tricyclic antidepressants (TCAs) may potentiate the risk of neurotoxicity and psychotic symptoms, even at therapeutic serum lithium levels. The mechanism is unknown but may be related to synergistic effects on neurotransmission. Mania, myoclonus, tremors, memory difficulties, confusion, serotonin syndrome, and a neuroleptic malignant-like syndrome have been reported in patients treated with TCAs several days after the addition of lithium, in most cases requiring discontinuation of either the TCA or lithium. Elderly patients may be particularly susceptible to this interaction.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium. Dosage adjustment or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22513/', '[1] Kojima H, Terao T, Yoshimura R "Serotonin syndrome during clomipramine and lithium treatment." Am J Psychiatry 150 (1993): 1897[2] Devanand DP, Sackeim HA, Brown RP "Myoclonus during combined tricyclic antidepressant and lithium treatment." J Clin Psychopharmacol 8 (1988): 446-7[3] Delisle JD "Rapid switch in a bipolar patient during lithium-tricyclic therapy." Am J Psychiatry 143 (1986): 1326-7[4] Austin LS, Arana GW, Melvin JA "Toxicity resulting from lithium augmentation of antidepressant treatment in elderly patients." J Clin Psychiatry 51 (1990): 344-5