INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45141, 'Bisoprolol', 'Zileuton', 'Moderate', 'Zileuton is believed to inhibit the hepatic metabolism of propranolol. Propranolol serum concentrations may increase by as much as 100% with a resultant increase in beta blockade.', NULL, 'It is unknown if this interaction will occur with other beta-blockers; however, if zileuton and a beta-blocker must be given concomitantly, the patient should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', NULL, 'It is unknown if this interaction will occur with other beta-blockers; however, if zileuton and a beta-blocker must be given concomitantly, the patient should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Metabolism', 'Acetylsalicylic acid, Atenolol, Ivabradine, Sotalol, Nadolol, Atenolol, Sotalol, Nadolol, Esmolol, Carteolol, Sotalol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45211/', '[1] "Product Information. Zylo Filmtab (zileuton)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
