INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45568, 'Erdafitinib', 'Calcium Phosphate', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', 'Calcium glucoheptonate, Calcium lactate, Calcium chloride, Calcium gluconate, Calcium glubionate anhydrous, Ascorbic acid, Calcium chloride', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', 