INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45578, 'Ginger', 'Fondaparinux', 'Moderate', 'Ginger may potentiate the effects of anticoagulants, platelet inhibitors and thrombolytic agents, possibly increasing the risk of bleeding. Limited data suggest that ginger may decrease platelet aggregation via the inhibition of thromboxane synthetase, although some studies have found no effect on platelet function or thromboxane production or activity.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used ginger and ginger supplements extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Cilostazol, Iloprost, Treprostinil, Desirudin, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45648/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30[2] Kruth P, Brosi E, Fux R, Morike K, Gleiter CH "