INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45592, 'Pyridoxine', 'Levodopa', 'Moderate', 'Pyridoxine doses of 5 mg/day or more antagonise the effects of levodopa. In the presence of pyridoxine, the peripheral metabolism of levodopa to dopamine is increased, resulting in less levodopa available to enter the CNS. The dopa-decarboxylase inhibitors carbidopa and benserazide reduce the peripheral metabolism of levodopa, even in the presence of high concentrations of pyridoxine, allowing more levodpa to enter the CNS.', NULL, 'The concomitant use of pyridoxine and levodopa without dopa-decarboxylase inhibitors should be avoided.', NULL, 'The concomitant use of pyridoxine and levodopa without dopa-decarboxylase inhibitors should be avoided. Patients should be advised against taking pyridoxine-containing vitamin supplements.', 'Antagonism', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, More', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45662/', '[1] "Product Information. Diclectin (doxylamine-pyridoxine)." Duchesnay Inc, Laval, IN.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Folic Acid (folic acid)." Method Pharmaceuticals, LLC, Arlington, TX.[6] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa