INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45665, 'Folic acid', 'Triamterene', 'Moderate', 'Folate antagonists such as sulfonamides, triamterene, trimethoprim, or pyrimethamine may decrease folic acid serum concentration. The mechanism may be related to inhibition of the enzyme dihydrofolate reductase by the folate antagonists, which converts folic acid into its active tetrahydrofolate form. In addition, it should be noted that folic acid doses greater than 2.5 mg may lead to treatment failure of antifolate antimalarials such as pyrimethamine.', NULL, 'Monitoring of patient response to folic acid supplementation if they are also taking folate antagonists is recommended.', NULL, 'Monitoring of patient response to folic acid supplementation if they are also taking folate antagonists is recommended. This may be especially important for pregnant women taking folic acid to reduce their risk of neural tube defects and in patients with megaloblastic anemia. Folic acid doses greater than 2.5 mg should be avoided in patients receiving pyrimethamine. Patients receiving antifolate antimalarials with folic acid should be monitored for possible treatment failure.', 'Antagonism', 'Cyanocobalamin, Iron protein succinylate, Iron, Ferrous gluconate, Ferric maltol, Iron sucrose, Ferrous fumarate, Sorbitol, Caffeine, Edrophonium, Indocyanine green, More', 'Eplerenone, Amiloride, Finerenone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45735/', '[1] "Product Information. Folic Acid (folic acid)." Method Pharmaceuticals, LLC, Arlington, TX.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Mulenga M, Malunga P, Bennett S. et.al "Folic acid treatment