INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45861, 'Duvelisib', 'Bortezomib', 'Moderate', 'Coadministration with duvelisib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by duvelisib. The interaction may be particularly important for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vinca alkaloids).', 'Metabolism', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, More', 'Entrectinib, Imatinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Lapatinib, Selumetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45931/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc., Needham, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45862, 'Echinacea', 'Bortezomib', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45932/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45863, 'Efalizumab', 'Bortezomib', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45933/', '[1] "Product Information. Raptiva (efalizumab)." Gen