INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22885, 'Amlodipine', 'Dapagliflozin', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', NULL, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', NULL, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22900/', '[1] "Product Information. Jardiance (empagliflozin)." Boehringer Ingelheim, Ridgefield, CT.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Farxiga (dapagliflozin)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Invokana (canagliflozin)." Janssen Pharmaceuticals, Titusville, NJ.[6] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[7] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[8] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[9] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[10] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[11] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6[12] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[13] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[14] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[15] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22886, 'Amlodipine', 'Darifenacin', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of darifenacin, which is a substrate of these isoenzymes.', NULL, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22901/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22887, 'Amlodipine', 'Darolutamide', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22902/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22888, 'Amlodipine', 'Darunavir', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22903/', '[1] Sasaki M, Maeda A, Fujimura A "Influence of diltiazem on the pharmacokinetics of amlodipine in elderly hypertensive patients." Eur J Clin Pharmacol 57 (2001): 85-6[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Norvasc (amlodipine)." Pfizer US Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22889, 'Amlodipine', 'Deferasirox', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism may involve induction of CYP450 3A4 activity by deferasirox, although in vitro, the drug has been shown to inhibit CYP450 3A4.', NULL, 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever deferasirox is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22904/', '[1] Skerjanec A, Wang J, Maren K, Rojkjaer L "Investigation of the pharmacokinetic interactions of deferasirox, a once-daily oral iron chelator, with midazolam, rifampin, and repaglinide in healthy volunteers." J Clin Pharmacol 50 (2010): 205-13[2] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
