INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22911, 'Amlodipine', 'Efavirenz', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by efavirenz.', NULL, 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever efavirenz is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22926/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22912, 'Amlodipine', 'Eliglustat', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', NULL, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22927/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767365203