INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22930, 'Amlodipine', 'Etravirine', 'Moderate', 'Coadministration with etravirine may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by etravirine.', NULL, 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever etravirine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22945/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22931, 'Amlodipine', 'Everolimus', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with amlodipine may increase the risk. The mechanism of this interaction has not been established. The onset of angioedema may sometimes be delayed up to two months following initiation of therapy. In addition, amlodipine, a weak CYP450 3A4 inhibitor, may increase the plasma concentrations of mTOR inhibitors, which are primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if amlodipine is administered with mTOR inhibitors.', NULL, 'Caution is advised if amlodipine is administered with mTOR inhibitors. Clinicians and patients should be aware of the potential for increased plasma levels as well as the risk of angioedema when mTOR inhibitors are prescribed with amlodipine. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever amlodipine is added to or withdrawn from therapy. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing. Patients should be monitored for development of other adverse effects including hypertension, hyperlipidemia, renal impairment, proteinuria, infections, and various types of malignancies.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22946/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365203);
