INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22995, 'Amlodipine', 'Lomitapide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23010/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22996, 'Amlodipine', 'Lonafarnib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing to or continuing lonafarnib at the starting dosage of 115 mg/m2 twice daily (for patients 12 months of age or older with a body surface area of at least 0.39 m2). Patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since lonafarnib exposures may be increased despite the dosage reduction and the effect on the QT interval is unknown. The previous dosage of lonafarnib may be resumed 14 days after discontinuing the weak CYP450 3A inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23011/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (22997, 'Amlodipine', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis. The extent to which other, less potent CYP450 3A4 inhibitors may affect lorlatinib is unknown.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23012/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365203);
