INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23058, 'Amlodipine', 'Pemigatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro. Increased exposure to pemigatinib may increase the incidence and severity of serious adverse reactions such as abdominal pain, acute kidney injury, cholangitis, cholangitis infective, diarrhea, failure to thrive, hypercalcemia, hyponatremia, pleural effusion, pyrexia, small intestinal obstruction, and urinary tract infection.', NULL, 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23073/', '[1] "Product Information. Pemazyre (pemigatinib)." Incyte Corporation, Wilmington, DE.[2] "Product Information. Pemazyre (pemigatinib)." Incyte Corporation, Wilmington, DE.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365203);
