INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23071, 'Amlodipine', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23086/', '[1] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.', 1767365203);
