INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23137, 'Amlodipine', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23152/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23138, 'Amlodipine', 'Tacrolimus', 'Moderate', 'Coadministration with amlodipine may increase the blood concentrations of tacrolimus. The exact mechanism of interaction is unknown but may involve inhibition of the CYP450 3A5 metabolism of tacrolimus.', NULL, 'Caution is advised if tacrolimus is administered concomitantly with amlodipine.', NULL, 'Caution is advised if tacrolimus is administered concomitantly with amlodipine. Tacrolimus blood levels should be monitored frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of amlodipine therapy in patients who are stabilized on their tacrolimus regimen. Patients should be advised to contact their physician if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23153/', '[1] Zhao W, Baudouin V, Fakhoury M, Storme T, Deschenes G, Jacqz-Aigrain E "Pharmacokinetic interaction between tacrolimus and amlodipine in a renal transplant child." Transplantation 93 (2012): e29-30[2] Khaira A, Rathi OP, Gupta A, et al. "Galactorrhoea and mastalgia in a renal transplant recipient on tacrolimus and amlodipine." Nephrology (Carlton) 14 (2009): 700-1[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Norvasc (amlodipine)." Pfizer US Pharmaceuticals, New York, NY.[6] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[7] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 1767365203);
