INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23186, 'Clarithromycin', 'Atorvastatin', 'Major', 'Some macrolide antibiotics inhibit CYP450 3A4 and may elevate the plasma concentrations of HMG-CoA reductase inhibitors that are metabolized by the isoenzyme. Macrolides that may significantly inhibit CYP450 3A4 include troleandomycin, erythromycin, and clarithromycin.', NULL, 'The benefits of using HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 in combination with medications that can inhibit the isoenzyme such as clarithromycin and erythromycin should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 in combination with medications that can inhibit the isoenzyme such as clarithromycin and erythromycin should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. A lower dosage of the HMG-CoA reductase inhibitor should be considered if concomitant use is required. Atorvastatin labeling recommends that the dosage not exceed 20 mg/day when used in combination with clarithromycin. Fluvastatin, pitavastatin, and rosuvastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacody