INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23191, 'Danazol', 'Atorvastatin', 'Major', 'The use of danazol with higher dosages of certain HMG-CoA reductase inhibitors may be associated with an increased risk of myopathy. The proposed mechanism is danazol inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of HMG-CoA reductase inhibitors like atorvastatin, lovastatin, simvastatin, as well as some of their pharmacologically active metabolites. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'The benefits of the use of atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) in patients receiving danazol should be carefully weighed against the potential risks.', NULL, 'The benefits of the use of atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) in patients receiving danazol should be carefully weighed against the potential risks. If the combination is prescribed, lower dosages of the HMG-CoA reductase inhibitor should be considered. Fluvastatin, pravastatin, and rosuvastatin are probably safer alternatives in patients receiving danazol, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23206/', '[1] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[2] Gruer PJK, Vega JM, Mercuri MF, Dobrinska MR, Tobert JA "Concomitant use of cytochrome P450 3A4 inhibitors and simvastatin." Am J Cardiol 84 (1999): 811-5[3] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.[4] Andreou ER, Ledger S "Potential drug interaction between simvastatin and danazol causing rhabdomyolysis." Can J Clin Pharmacol 10 (2003): 172-4[5] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[6] Dallaire M, Chamberland M "Severe rhabdomyolysis in a patient receiving lovastatin, danazol and doxycycline." Can Med Assoc J 150 (1994): 1991-4[7] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[8] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[9] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[10] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[11] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[12] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[13] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[14] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[15] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23192, 'Darunavir', 'Atorvastatin', 'Major', 'Coadministration with darunavir/ritonavir may significantly increase the plasma concentrations of atorvastatin. The mechanism is inhibition of the CYP450 3A4-mediated metabolism of atorvastatin by darunavir/ritonavir. High levels of statin or HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Atorvastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir.', NULL, 'Atorvastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir. The dosage may be increased gradually based on clinical response and tolerance, but should not exceed 20 mg/day. Fluvastatin and rosuvastatin may be safer alternatives in patients treated with protease inhibitors, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/2