INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23207, 'Atorvastatin', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton. Caution is advised because these agents may also have additive hepatotoxic effects with lomitapide. Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin monitored prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23222/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[5] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9[6] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[7] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[8] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23208, 'Atorvastatin', 'Mipomersen', 'Major', 'Coadministration of mipomersen with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Mipomersen can cause elevations in serum transaminases and hepatic steatosis.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, lomitapide, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Mipomersen has not been studied with other LDL-lowering agents that can also increase hepatic fat, thus concomitant use is not recommended. Patients treated with mipomersen should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking mipomersen not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23223/', '[1] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23209, 'Atorvastatin', 'Nefazodone', 'Major', 'Coadministration with nefazodone may significantly increase the plasma concentrations of HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 such as atorvastatin, cerivastatin, lovastatin, and simvastatin. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nefazodone. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death. There have been case reports of patients treated with simvastatin who developed severe myopathy or rhabdomyolysis during coadministration of nefazodone.', NULL, 'Nefazodone should generally not be used in combination with atorvastatin, cerivastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) due to the potential for severe interaction.', NULL, 'Nefazodone should generally not be used in combination with atorvastatin, cerivastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) due to the potential for severe interaction. Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with nefazodone, since they are not metabolized by CYP450 3A4, although pravastatin was cited in a case report of suspected interaction that resulted in asymptomatic increases in creatine kinase levels. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark-colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23224/', '[1] Alderman CP "Possible interaction between nefazodone and pravastatin." Ann Pharmacother 34 (2000): 538-9[2] Corsini A, Bellosta S, Baetta R, Fumagalli R, Paoletti R, Bernini F "New insights into the pharmacodynamic and pharmacokinetic properties of statins." Pharmacol Ther 84 (1999): 413-28[3] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[4] Skrabal MZ, Stading JA, Monaghan MS "Rhabdomyolysis associated with simvastatin-nefazodone therapy." South Med J 96 (2003): 1034-5[5] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[6] Jacobson RH, Wang P, Glueck CJ "Myositis and rhabdomyolysis associated with concurrent use of simvastatin and nefazodone." JAMA 277 (1997): 296[7] Karnik NS, Maldonado JR "Antidepressant and statin interactions: a review and case report of simvastatin and nefazodone-induced rhabdomyolysis and transaminitis." Psychosomatics 46 (2005): 565-8[8] Jody DN "Myositis and rhabdomyolysis associated with concurrent use of simvastatin and nefazodone." JAMA 277 (1997): 296-7[9] Thompson M, Samuels S "Rhabdomyolysis with simvastatin and nefazodone." Am J Psychiatry 159 (2002): 1607[10] Bottorf MB "Possible interaction between nefazodone and pravastatin." Ann Pharmacother 34 (2000): 538[11] Alderman CP "Possible interaction between nefazodone and pravastatin." Ann Pharmacother 33 (1999): 871[12] Paoletti R, Corsini A, B