INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23212, 'Pexidartinib', 'Atorvastatin', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23227/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23213, 'Posaconazole', 'Atorvastatin', 'Major', 'Coadministration with posaconazole may significantly increase the plasma concentrations of HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 such as atorvastatin, cerivastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin). The mechanism is decreased clearance due to inhibition of CYP450 3A4 by posaconazole. The interaction has been studied with simvastatin. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'The use of posaconazole in combination with HMG-CoA reductase inhibitors that are primarily metabolized by CYP450 3A4 is considered contraindicated.', NULL, 'The use of posaconazole in combination with HMG-CoA reductase inhibitors that are primarily metabolized by CYP450 3A4 is considered contraindicated. Fluvastatin, pitavastatin, pravastatin, and rosuvastatin may be suitable alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23228/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Neuvonen PJ, Kantola T, Kivisto KT "Simvastatin but not pravastatin is very susceptible to interaction with the CYP3A4 inhibitor itraconazole." Clin Pharmacol Ther 63 (1998): 332-41[3] Horn M "Coadministration of itraconazole with hypolipidemic agents may induce rhabdomyolysis in healthy individuals." Arch Dermatol 132 (1996): 1254[4] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[5] Lees RS, Lees AM "Rhabdomyolysis from the coadministration of lovastatin and the antifungal agent itraconazole." N Engl J Med 333 (1995): 664-5[6] Neuvonen PJ, Jalava KM "Itraconazole drastically increases plasma concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 60 (1996): 54-61[7] Gilad R, Lampl Y "Rhabdomyolysis induced by simvastatin and ketoconazole treatment." Clin Neuropharmacol 22 (1999): 295-7[8] Kivisto KT, Kantola T, Neuvonen PJ "Different effects of itraconazole on the pharmacokinetics of fluvastatin and lovastatin." Br J Clin Pharmacol 46 (1998): 49-53[9] Kantola T, Kivisto KT, Neuvonen PJ "Effect of itraconazole on the pharmacokinetics of atorvastatin." Clin Pharmacol Ther 64 (1998): 58-65[10] Lomaestro BM, Piatek MA "Update on drug interactions with azole antifungal agents." Ann Pharmacother 32 (1998): 915-28[11] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.[12] Cerner Multum, Inc. "Australian Product Information." O 0[13] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[14] Kantola T, Kivisto KT, Neuvonen PJ "Effect of itraconazole on cerivastatin pharmacokinetics." Eur J Clin Pharmacol 54 (1999): 851-5[15] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[16] Neuvonen PJ, Backma