INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23365, 'Benazepril', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23382/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23366, 'Benazepril', 'Pergolide', 'Moderate', 'One patient, whose hypertension had been treated with lisinopril for three years, experienced a severe hypotensive episode after receiving a 0.05 mg dose of pergolide. The mechanism is unknown and causality has not been clearly established.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors. Dose adjustments may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23383/', '[1] Kando JC, Keck PE Jr, Wood PA "Pergolide-induced hypotension" DICP 24 (1990): 543[2] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[3] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[4] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23367, 'Benazepril', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23384/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23368, 'Benazepril', 'Phenoxybenzamine', 'Moderate', 'Additive hypotensive effects may occur when ACE inhibitors are used in combination with alpha-blockers. In the presence of ACE inhibition, the risk and/or severity of first-dose effects associated with alpha-blockers such as postural hypotension and syncope may be increased.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor. Small initial dosages of the alpha-blocker should be considered and gradually titrated to desired effect, while the ACE inhibitor dosage may also need to be reduced. Hemodynamic responses should be monitored, especially during the first few weeks of therapy. Taking the alpha-blocker at bedtime may minimize the occurrence of orthostatic effects. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23385/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] Baba T, Tomiyama T, Takebe K "Enhancement by an ACE inhibitor of first-dose hypotension caused by an alpha-blocker." N Engl J Med 322 (1990): 1237[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[5] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23369, 'Benazepril', 'Phenylbutazone', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate',