INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23474, 'Dexfenfluramine', 'Celecoxib', 'Moderate', 'Coadministration with celecoxib may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by celecoxib.', NULL, 'Caution is advised if celecoxib must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if celecoxib must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever celecoxib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23491/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23475, 'Dextromethorphan', 'Celecoxib', 'Moderate', 'Coadministration with CYP450 2D6 inhibitors may increase the plasma concentrations of dextromethorphan in patients who are extensive metabolizers of this isoenzyme (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). The proposed mechanism is inhibition of the CYP450 2D6-mediated O-demethylation of dextromethorphan. Increased plasma concentrations increase the risk of dextromethorphan-related adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome.', NULL, 'Caution should be exercised if these drugs must be used together.', NULL, 'Caution should be exercised if these drugs must be used together. Patients should be monitored for signs of dextromethorphan adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome, and advised to notify their health care professional if these adverse effects develop or worsen. Dose reduction of dextromethorphan may also be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23492/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Funck-Brentano C, Jacqz-Aigrain E, Leenhardt A, Roux A, Poirier JM, Jaillon P "Influence of amiodarone on genetically determined drug metabolism in humans." Clin Pharmacol Ther 50 (1991): 259-66[5] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[6] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23476, 'Diatrizoate', 'Celecoxib', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23493/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Clin Evid 9 (2008): 2001[4] van den Berk G, Tonino S, de Fijter C, Smit W, Schultz MJ "Bench-to-bedside review: Preventative measures for contrast-induced nephropathy in critically ill patients." Crit Care 9 (2005): 361-70[5] Marcos LA, Camins BC, Ritchie DJ, Casabar E, Warren DK "Acute renal insufficiency during telavancin therapy in clinical practice." J Antimicrob Chemother 67 (2012): 723-6[6] Bansal R, Kaplan AA, Aflieco F "Contrast-Induced Nephropathy. Available from: URL: http://emedicine.medscape.com/article/246751-overview." ([2016, Jan 18]):[7] Bennett WM, Porter GA "Nephrotoxicity of common drugs used by urologists." Urol Clin North Am 17 (1990): 145-56', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23477, 'Dicoumarol', 'Celecoxib', 'Moderate', 'Coadministration with celecoxib may infrequently enhance the hypoprothrombinemic effect of warfarin. The proposed mechanism is competitive inhibition of the CYP450 2C9 metabolism of warfarin by celecoxib, as both drugs are substrates of this isoenzyme.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with celecoxib.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with celecoxib. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of celecoxib in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23494/', '[1] Stoner SC, Lea JW, Dubisar BM, Farrar C "Possible international normalized ratio elevation associated with celecoxib and warfarin in an elderly psychi