    ) VALUES (1337710, 'Iron sucrose', 'Alendronic acid', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of bisphosphonates. The proposed mechanism is chelation of bisphosphonates to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Alendronate should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Alendronate should be administered at least 1 hour before sucroferric oxyhydroxide. No dosing recommendations are available for use with other bisphosphonates. Patients may follow the manufacturers'' suggestion provided in the individual bisphosphonate label regarding use with mineral supplements and other polyvalent cation-containing products (i.e., administer sucroferric oxyhydroxide at least 30 minutes after risedronate, 1 hour after ibandronate, and at least 2 hours before or 2 hours after clodronate, etidronate, or tiludronate).', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
