        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1337770, 'Cholecalciferol', 'Sevelamer', 'Moderate', 'INTERVAL: Bile acid sequestrants and the phosphate binder, sevelamer, can decrease the absorption of fat-soluble vitamins A, D, E, and K. In non-clinical safety studies, rats administered colesevelam at doses greater than 30-fold the projected human clinical dose developed hemorrhage in association with vitamin K deficiency. In a crossover study involving healthy subjects, coadministration of sevelamer with calcitriol resulted in a significant reduction in bioavailability for calcitriol (calcitriol with sevelamer vs calcitriol alone: AUC 137 pg*h/mL vs 318 pg*h/mL and Cmax 40.1 pg/mL vs 49.7 pg/mL, respectively).', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
