INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23501, 'Flucytosine', 'Celecoxib', 'Moderate', 'Drugs that impair glomerular filtration may prolong the half-life of flucytosine and lead to accumulation.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23518/', '[1] "Product Information. Ancobon (flucytosine)." Roche Laboratories, Nutley, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23502, 'Gatifloxacin', 'Celecoxib', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23519/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23503, 'Gentamicin', 'Celecoxib', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23520/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365203);
