INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23643, 'Bempedoic acid', 'Olmesartan', 'Moderate', 'Coadministration with bempedoic acid may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the uptake transporters organic anion transporting polypeptide (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, is decreased clearance due to bempedoic acid-mediated inhibition of OATP1B1 and 1B3.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever bempedoic acid is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23661/', '[1] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[2] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23644, 'Captopril', 'Olmesartan', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', NULL, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', NULL, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23662/', '[1] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Micardis (telmisartan)." Boehringer-Ingelheim, Ridgefield, CT.[3] Rossing K, Jacobsen P, Pietraszek L, Parving HH "Renoprotective effects of adding angiotensin II receptor blocker to maximal recommended doses of ACE inhibitor in diabetic nephropathy: a randomized double-blind crossover tr