INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23847, 'Amobarbital', 'Bupropion', 'Moderate', 'Excessive use or abrupt discontinuation of barbiturates and other sedatives after chronic ingestion may precipitate seizures in patients receiving bupropion. Theoretically, barbiturates may also decrease the plasma concentrations and pharmacologic effects of bupropion due to induction of hepatic CYP450 isoenzymes.', NULL, 'Pharmacologic response to bupropion should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', NULL, 'Pharmacologic response to bupropion should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary. Although barbiturates and other sedatives may be prescribed to treat agitation, anxiety, and insomnia associated with bupropion use, patients should be alerted to the possibility of an increased risk of seizures during excessive exposure to these drugs. Patients should not attempt to alter the dosages or discontinue the medications on their own without consulting with their physician. The use of bupropion is contraindicated in patients undergoing abrupt discontinuation of sedatives.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23866/', '[1] "Product Information. Wellbutrin (bupropion)." Glaxo Wellcome, Research Triangle Park, NC.[2] James WA, Lippmann S "Bupropion: overview and prescribing guidelines in depression." South Med J 84 (1991): 222-4[3] "Product Information. Wellbutrin (bupropion)." Glaxo Wellcome, Research Triangle Park, NC.[4] Posner J, Bye A,