INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23851, 'Amobarbital', 'Butorphanol', 'Major', 'Barbiturates may potentiate the central nervous system (CNS) depressant effects of opioids. Concomitant use may result in profound sedation, respiratory depression, coma, and death. On the other hand, some barbiturates can also induce the hepatic metabolism of opioids that are metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, methadone and oxycodone, resulting in enhanced clearance. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on their opioid regimen following the addition of a barbiturate. Conversely, discontinuation of the barbiturate may increase plasma concentrations of the opioid and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate. Particular caution is advisable when a barbiturate is added to or withdrawn from therapy in patients receiving opioids that are CYP450 3A4 substrates, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the barbiturate and overdose following discontinuation. A dosage adjustment for one or both drugs may be required.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23870/', '[1] "Product Information. Apadaz (acetaminophen-benzhydrocodone)." KemPharm, Inc, Coralville, IA.[2] Liu S-J, Wang RI "Case report of barbiturate-induced enhancement of methadone metabolism and withdrawal syndrome." Am J Psychiatry 141 (1984): 1287-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Butorphanol Tartrate (butorphanol)." Apotex Corporation, Weston, FL.[7] "Product Information. Ionsys (fentanyl)." Ortho McNeil Pharmaceutical, Raritan, NJ.[8] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988): 623-9[9] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.[10] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[11] "Product Information. Olinvyk (oliceridine)." Trevena Inc, Chesterbrook, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23852, 'Amobarbital', 'Cabazitaxel', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme. However, no formal drug interaction studies have been conducted.', NULL, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown. Caution is advised if they are used with cabazitaxel.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23871/', '[1] "Product Information. Jevtana (cabazitaxel)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Jevtana (cabazitaxel)." sanofi-aventis , Bridgewater, NJ.[3] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365203);
