INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (23884, 'Amobarbital', 'Clonazepam', 'Moderate', 'Barbiturates may increase the plasma clearance of clonazepam and thereby reduce its clinical effectiveness. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion. Also, clonazepam may increase the effectiveness of barbiturates. A case of respiratory depression and coma has been reported in a patient receiving high doses of both clonazepam and amobarbital.', NULL, 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', NULL, 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23903/', '[1] Khoo KC, Mendels J, Rothbart M, et al. "Influence of phenytoin and phenobarbital on the disposition of a single oral dose of clonazepam." Clin Pharmacol Ther 28 (1980): 368-75[2] Honer WG, Rosenberg RG, Turey M, Fisher WA "Respiratory failure after clonazepam and amobarbital ." Am J Psychiatry 143 (1986): 1495[3] Nanda RN, Johnson RH, Keogh HJ, Lambie DG, Melville ID "Treatment of epilepsy with clonazepam and its effect on other anticonvulsants." J Neurol Neurosurg Psychiatry 40 (1977): 538-43', 1767365203);
