INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24258, 'Amoxicillin', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24277/', '[1] "Product Information. Colazal (balsalazide)" Salix Pharmaceuticals, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24259, 'Amoxicillin', 'Bifidobacterium longum infantis', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24278/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24260, 'Amoxicillin', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24279/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767365203);
