INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24314, 'Amphetamine', 'Cinacalcet', 'Moderate', 'Coadministration with cinacalcet may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by cinacalcet, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', NULL, 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.', NULL, 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.g., class IC antiarrhythmic agents, phenothiazines, certain beta blockers, and most tricyclic antidepressants). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cinacalcet is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24333/', '[1] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sensipar (cinacalcet)." Amgen USA, Thousand Oaks, CA.[4] "Product Information. Sensipar (cinacalcet)." Amgen USA, Thousand Oaks, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24315, 'Amphetamine', 'Citalopram', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', NULL, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', NULL, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24334/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] Barrett J, Meehan O, Fahy T "SSRI and sympathomimetic interaction." Br J Psychiatry 168 (1996): 253[3] Prior FH, Isbister GK, Dawson AH, Whyte IM "Serotonin toxicity with therapeutic doses of dexamphetamine and venlafaxine." Med J Aust 176 (2002): 240-1[4] Walters AM "Sympathomimetic-fluoxetine interaction." J Am Acad Child Adolesc Psychiatry 31 (1992): 565-6[5] Bostwick JM, Brown TM "A toxic reaction from combining fluoxetine and phentermine." J Clin Psychopharmacol 16 (1996): 189-90[6] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[7] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[8] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[9] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.[10] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[12] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365203);
