INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24322, 'Amphetamine', 'Dacomitinib', 'Moderate', 'Coadministration with dacomitinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 2D6 by dacomitinib.', NULL, 'Caution is advised if dacomitinib is used in combination with CYP450 2D6 substrates.', NULL, 'Caution is advised if dacomitinib is used in combination with CYP450 2D6 substrates. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever dacomitinib is added to or withdrawn from therapy. Avoid concomitant use with dacomitinib where minimal increases in concentration of the CYP450 2D6 substrate may lead to serious or life-threatening toxicities.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24341/', '[1] "Product Information. Vizimpro (dacomitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24323, 'Amphetamine', 'Darifenacin', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of the isoenzyme by darifenacin. Conversely, some CYP450 2D6 substrates may also increase the plasma concentrations of darifenacin, which is itself metabolized by CYP450 2D6 and may participate in enzymatic competitive inhibition with other substrates.', NULL, 'Caution is advised if darifenacin must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if darifenacin must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darifenacin is added to or withdrawn from therapy. Pharmacologic response to darifenacin should also be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their doctor if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24342/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
