INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24351, 'Glycerol phenylbutyrate', 'Amphetamine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24370/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24352, 'Guanadrel', 'Amphetamine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24371/', '[1] Gerkens JF, McCulloch MW, Wilson J "Mechanism of the antagonism between guanethidine and dexamphetamine." Br J Pharmacol 35 (1969): 563-72[2] Flegin OT, Morgan DH, Oates JA, Shand DG, Turner P "The mechanism of the reversal of the effect of guanethidine by amphetamines in cat and man." Br J Pharmacol 39 (1970): p253[3] Gulati OD, Dave BT, Gokhale SD, Shah KM "Antagonism of adrenergic neuron blockade in hypertensive subjects." Clin Pharmacol Ther 7 (1966): 510-4[4] Sherman GP, Walton CA "Adrenergic transmission and drug interaction." J Am Pharm Assoc 15 (1975): 86-90[5] Obianwu HO "Some studies on the mechanism by which d-amphetamine antagonizes guanethidine induced adrenergic neurone blockade." Acta Physiol Scand 75 (1969): 102-10[6] Deshmankar BS, Leewis JA "Ventricular tachycardia associated with the administration of methylphenidate during guanethidine therapy." Can Med Assoc J 97 (1967): 1166-71[7] Follenfant MJ, Robson RD "The antagonism of adrenergic neurone blockade by amphetamine and dexamphetamine in the rat and guinea-pig." Br J Pharmacol 38 (1970): 792[8] Ober KF, Wang RI "Drug interactions with guanethidine." Clin Pharmacol Ther 14 (1973): 190-5[9] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[10] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[11] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767365203);
