INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1338153, 'Alfentanil', 'Propofol', 'Moderate', 'Additive central nervous system and cardiorespiratory depressant effects may occur when fospropofol or propofol is administered with alfentanil. Also, when combined to provide general anesthesia and analgesia, this combination has demonstrated synergistic activity.', 'DDInter', 1767317475, 'To prevent intraoperative overdose and prolongation of recovery, the dosage of alfentanil given for suppression of surgical stimuli should be reduced when fospropofol or propofol is coadministered. Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly. Supportive therapy should be provided if needed.', 'Synergism', 'To prevent intraoperative overdose and prolongation of recovery, the dosage of alfentanil given for suppression of surgical stimuli should be reduced when fospropofol or propofol is coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
